INSERT INTO `wp_posts` VALUES (45,1,'2025-09-18 10:12:02','2025-09-18 09:12:02','<!-- wp:cover {\"url\":\"http://clubdeportivoetsit.local/wp-content/uploads/2025/09/image.png\",\"id\":46,\"dimRatio\":50,\"style\":{\"color\":[]}} -->\n<div class=\"wp-block-cover\"><img class=\"wp-block-cover__image-background wp-image-46\" alt=\"\" src=\"http://clubdeportivoetsit.local/wp-content/uploads/2025/09/image.png\" data-object-fit=\"cover\"/><span aria-hidden=\"true\" class=\"wp-block-cover__background has-background-dim\"></span><div class=\"wp-block-cover__inner-container\"><!-- wp:paragraph {\"align\":\"center\",\"fontSize\":\"large\"} -->\n<p class=\"has-text-align-center has-large-font-size\"><em><strong>HAZTE SOCIO DEL CLUB DEPORTIVO</strong></em></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:group {\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group\"><!-- wp:paragraph -->\n<p>Hazte socio del Club Deportivo&nbsp;y disfruta de todas las ventajas que ofrecemos en la asociación de alumnos mas numerosa de la ETSIT!</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:group -->\n\n<!-- wp:paragraph -->\n<p>Ven al club cualquier día, estaremos allí dispuestos a ayudarte y resolverte las dudas! No pierdas la oportunidad de utilizar nuestro PingPong y nuestro futbolín! Venid&nbsp;a conocernos!!</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>PARA APUNTARTE BAJA AL CLUB:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul class=\"wp-block-list\"><!-- wp:list-item -->\n<li>6 Euros anuales para los socios de anteriores años</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>8 Euros anuales para nuevos socios</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph {\"className\":\"has-text-align-center has-large-font-size\"} -->\n<p class=\"has-text-align-center has-large-font-size\">Correo: deporteleco@gmail.com</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"align\":\"center\",\"fontSize\":\"large\"} -->\n<p class=\"has-text-align-center has-large-font-size\">Instagram: cdetsit</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:cover -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->','INICIO','','publish','closed','closed','','inicio','','','2025-09-18 10:44:44','2025-09-18 09:44:44','',0,'http://clubdeportivoetsit.local/?page_id=45',0,'page','',0);
